import React, { useState } from 'react';
import './Press.css';

const Press = () => {
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 10;

  // 샘플 프레스 데이터
  const pressData = [
    {
      id: 1,
      title: "운생동건축사사무소, 국제 건축상 수상",
      created_at: "2024-01-20",
      link: "https://www.architecturalrecord.com/articles/15234/unsangdong-architects-wins-international-award",
      newspaper: "Architectural Record"
    },
    {
      id: 2,
      title: "한국 현대건축의 새로운 지평을 열다",
      created_at: "2024-01-18",
      link: "https://www.dezeen.com/2024/01/18/unsangdong-architects-korean-contemporary-architecture/",
      newspaper: "Dezeen"
    },
    {
      id: 3,
      title: "지속가능한 건축 디자인으로 주목받는 운생동",
      created_at: "2024-01-16",
      link: "https://www.archdaily.com/995234/sustainable-architecture-unsangdong-architects",
      newspaper: "ArchDaily"
    },
    {
      id: 4,
      title: "운생동건축사사무소의 혁신적 도시재생 프로젝트",
      created_at: "2024-01-14",
      link: "https://www.worldarchitecturenews.com/article/1234567/unsangdong-urban-regeneration",
      newspaper: "World Architecture News"
    },
    {
      id: 5,
      title: "아시아 건축계의 떠오르는 스타, 운생동건축",
      created_at: "2024-01-12",
      link: "https://www.architectural-review.com/essays/unsangdong-rising-star-asian-architecture",
      newspaper: "Architectural Review"
    },
    {
      id: 6,
      title: "문화시설 설계의 새로운 패러다임 제시",
      created_at: "2024-01-10",
      link: "https://www.metropolismag.com/architecture/unsangdong-cultural-facilities-design/",
      newspaper: "Metropolis Magazine"
    },
    {
      id: 7,
      title: "운생동의 공공건축, 시민과 소통하는 공간",
      created_at: "2024-01-08",
      link: "https://www.architecturalrecord.com/articles/15235/unsangdong-public-architecture",
      newspaper: "Architectural Record"
    },
    {
      id: 8,
      title: "젊은 건축가들이 주목하는 운생동의 디자인 철학",
      created_at: "2024-01-06",
      link: "https://www.dezeen.com/2024/01/06/unsangdong-design-philosophy-young-architects/",
      newspaper: "Dezeen"
    },
    {
      id: 9,
      title: "운생동건축사사무소, 베니스 비엔날레 참가 확정",
      created_at: "2024-01-04",
      link: "https://www.archdaily.com/995235/unsangdong-venice-biennale-2024",
      newspaper: "ArchDaily"
    },
    {
      id: 10,
      title: "한국 건축의 미래를 제시하는 운생동의 비전",
      created_at: "2024-01-02",
      link: "https://www.architectural-review.com/essays/unsangdong-future-korean-architecture",
      newspaper: "Architectural Review"
    },
    {
      id: 11,
      title: "운생동의 주거 프로젝트, 국제적 인정받아",
      created_at: "2023-12-30",
      link: "https://www.worldarchitecturenews.com/article/1234568/unsangdong-residential-project",
      newspaper: "World Architecture News"
    },
    {
      id: 12,
      title: "건축과 예술의 경계를 허무는 운생동의 실험",
      created_at: "2023-12-28",
      link: "https://www.metropolismag.com/architecture/unsangdong-architecture-art-boundaries/",
      newspaper: "Metropolis Magazine"
    }
  ];

  const totalCount = pressData.length;
  const totalPages = Math.ceil(totalCount / itemsPerPage);
  const pageGroupSize = 5;
  const currentGroup = Math.floor((currentPage - 1) / pageGroupSize);
  const startPage = currentGroup * pageGroupSize + 1;
  const endPage = Math.min(startPage + pageGroupSize - 1, totalPages);

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    const year = date.getFullYear().toString().slice(-2); // 년도 뒤 2자리
    const month = String(date.getMonth() + 1).padStart(2, '0'); // 월 2자리
    const day = String(date.getDate()).padStart(2, '0'); // 일 2자리
    return `${year}/${month}/${day}`;
  };

  const handlePressClick = (press) => {
    console.log('프레스 클릭:', press.title);
    window.open(press.link, '_blank', 'noopener,noreferrer');
  };

  const handlePageChange = (page) => {
    setCurrentPage(page);
    window.scrollTo(0, 0);
  };

  const renderPagination = () => {
    if (totalPages <= 1) return null;

    return (
      <div className="pagination">
        {totalPages > pageGroupSize && currentGroup > 0 && (
          <button 
            onClick={() => handlePageChange(startPage - 1)}
          >
            ←
          </button>
        )}
        {Array.from(
          { length: endPage - startPage + 1 }, 
          (_, i) => startPage + i
        ).map(page => (
          <button
            key={page}
            onClick={() => handlePageChange(page)}
            className={currentPage === page ? 'active' : ''}
          >
            {page}
          </button>
        ))}
        {totalPages > pageGroupSize && endPage < totalPages && (
          <button 
            onClick={() => handlePageChange(endPage + 1)}
          >
            →
          </button>
        )}
      </div>
    );
  };

  const renderPressRow = (press) => (
    <tr 
      key={press.id} 
      onClick={() => handlePressClick(press)} 
      style={{ cursor: 'pointer' }}
    >
      <td className="press-title">
        {press.title}
      </td>
      <td className="press-date">{formatDate(press.created_at)}</td>
    </tr>
  );

  // 현재 페이지에 해당하는 프레스 가져오기
  const startIndex = (currentPage - 1) * itemsPerPage;
  const endIndex = startIndex + itemsPerPage;
  const currentPress = pressData.slice(startIndex, endIndex);

  return (
    <div className="press-container">
      <h1 className="press-page-title">PRESS</h1>
      <div className="press-board">
        <table className="press-table">
          <tbody>
            {currentPress.length > 0 ? (
              currentPress.map(press => renderPressRow(press))
            ) : (
              <tr>
                <td colSpan="2" className="no-data">등록된 프레스가 없습니다.</td>
              </tr>
            )}
          </tbody>
        </table>
        {renderPagination()}
      </div>
    </div>
  );
};

export default Press;
