import React, { useState, useEffect } from 'react';
import './SearchResults.css';

function SearchResults({ searchQuery, onNavigate }) {
  const [searchTerm, setSearchTerm] = useState(searchQuery || '');

  // 모든 프로젝트 데이터 (Architecture, Art, Design)
  const allProjects = [
    // Architecture 프로젝트 (100-199)
    {
      id: 101,
      title: "오동숲속도서관",
      titleEn: "Odong Public Library",
      year: "2024",
      location: "서울 성북구 Seoul Seongbuk-gu",
      type: "HOUSE",
      client: "Client name",
      director: "Yoongyoo Jang, Changhoon Shin",
      status: "Completed",
      category: "Architecture",
      image: "./images/Architecture/arch-1.jpg"
    },
    {
      id: 102,
      title: "이상봉 타워",
      titleEn: "Lie Sang Bong Tower",
      year: "2023",
      location: "서울 성북구 Seoul Seongbuk-gu",
      type: "Cultural",
      client: "Client name",
      director: "Yoongyoo Jang, Changhoon Shin",
      status: "Completed",
      category: "Architecture",
      image: "./images/Architecture/arch-2.jpg"
    },
    {
      id: 103,
      title: "헤이리, 코스미코",
      titleEn: "Heyri COSMICO",
      year: "2022",
      location: "서울 성북구 Seoul Seongbuk-gu",
      type: "Museum",
      client: "Client name",
      director: "Yoongyoo Jang, Changhoon Shin",
      status: "Completed",
      category: "Architecture",
      image: "./images/Architecture/arch-3.jpg"
    },
    {
      id: 104,
      title: "오동숲속도서관",
      titleEn: "Odong Public Library",
      year: "2024",
      location: "서울 성북구 Seoul Seongbuk-gu",
      type: "House",
      client: "Client name",
      director: "Yoongyoo Jang, Changhoon Shin",
      status: "Completed",
      category: "Architecture",
      image: "./images/Architecture/arch-1.jpg"
    },
    {
      id: 105,
      title: "이상봉 타워",
      titleEn: "Lie Sang Bong Tower",
      year: "2023",
      location: "서울 성북구 Seoul Seongbuk-gu",
      type: "Cultural",
      client: "Client name",
      director: "Yoongyoo Jang, Changhoon Shin",
      status: "Completed",
      category: "Architecture",
      image: "./images/Architecture/arch-2.jpg"
    },
    {
      id: 106,
      title: "헤이리, 코스미코",
      titleEn: "Heyri COSMICO",
      year: "2022",
      location: "서울 성북구 Seoul Seongbuk-gu",
      type: "Museum",
      client: "Client name",
      director: "Yoongyoo Jang, Changhoon Shin",
      status: "Completed",
      category: "Architecture",
      image: "./images/Architecture/arch-3.jpg"
    },
    // Art 프로젝트 (200-299)
    {
      id: 201,
      title: "인간산수",
      titleEn: "Human, Space, Mountain, Water",
      year: "2024",
      location: "서울 성북구 Seoul Seongbuk-gu",
      type: "HOUSE",
      client: "Client name",
      director: "Yoongyoo Jang, Changhoon Shin",
      status: "Completed",
      category: "Art",
      image: "./images/Art/art-1.jpg"
    },
    {
      id: 202,
      title: "인간산수",
      titleEn: "Human, Space, Mountain, Water",
      year: "2024",
      location: "서울 성북구 Seoul Seongbuk-gu",
      type: "HOUSE",
      client: "Client name",
      director: "Yoongyoo Jang, Changhoon Shin",
      status: "Completed",
      category: "Art",
      image: "./images/Art/art-2.jpg"
    },
    {
      id: 203,
      title: "인간산수",
      titleEn: "Human, Space, Mountain, Water",
      year: "2024",
      location: "서울 성북구 Seoul Seongbuk-gu",
      type: "EXHIBITION",
      client: "Client name",
      director: "Yoongyoo Jang, Changhoon Shin",
      status: "Completed",
      category: "Art",
      image: "./images/Art/art-3.jpg"
    }
  ];

  // 검색 필터링 함수
  const filterProjects = (query) => {
    if (!query || query.trim() === '') {
      return [];
    }

    const searchTermLower = query.toLowerCase().trim();

    return allProjects.filter(project => {
      return (
        project.title.toLowerCase().includes(searchTermLower) ||
        project.titleEn.toLowerCase().includes(searchTermLower) ||
        project.type.toLowerCase().includes(searchTermLower) ||
        project.category.toLowerCase().includes(searchTermLower) ||
        project.year.includes(searchTermLower) ||
        project.location.toLowerCase().includes(searchTermLower) ||
        project.client.toLowerCase().includes(searchTermLower) ||
        project.director.toLowerCase().includes(searchTermLower) ||
        project.status.toLowerCase().includes(searchTermLower)
      );
    });
  };

  const [filteredProjects, setFilteredProjects] = useState(() => filterProjects(searchQuery));

  // searchQuery가 변경될 때마다 필터링 업데이트
  useEffect(() => {
    setSearchTerm(searchQuery || '');
    setFilteredProjects(filterProjects(searchQuery));
  }, [searchQuery]);

  const handleProjectClick = (projectId) => {
    onNavigate('project-detail', projectId);
  };

  const handleSearchSubmit = (e) => {
    e.preventDefault();
    setFilteredProjects(filterProjects(searchTerm));
  };

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  return (
    <div className="search-results-container">
      <div className="search-header">
        <h1 className="search-page-title">SEARCH RESULTS</h1>

        {/* 검색 결과 정보 */}
        <div className="search-info">
          {searchTerm && (
            <p className="search-query">
              "{searchTerm}" 검색 결과: {filteredProjects.length}개
            </p>
          )}
        </div>
      </div>

      {/* 검색 결과 */}
      <div className="search-results-content">
        {!searchTerm || searchTerm.trim() === '' ? (
          <div className="no-search-term">
            <p>검색어를 입력해주세요.</p>
          </div>
        ) : filteredProjects.length === 0 ? (
          <div className="no-results">
            <p>"{searchTerm}"에 대한 검색 결과가 없습니다.</p>
          </div>
        ) : (
          <div className="projects-grid">
            {filteredProjects.map((project) => (
              <div 
                key={project.id} 
                className="search-project-item"
                onClick={() => handleProjectClick(project.id)}
              >
                <div className="search-project-image-wrapper">
                  <img 
                    src={project.image} 
                    alt={project.title}
                    className="search-project-image"
                  />
                  <div className="search-project-overlay">
                    <div className="search-project-info">
                      <div className="search-project-category">{project.category}</div>
                      <div className="search-project-title-wrap">
                        <h3 className="search-project-title">{project.title}</h3>
                        <h4 className="search-project-title-en">{project.titleEn}</h4>
                      </div>
                      <div className="search-project-details">
                        <div className="search-project-detail-item">
                          <span className="search-project-detail-label">Year:</span>
                          <span className="search-project-detail-value">{project.year}</span>
                        </div>
                        <div className="search-project-detail-item">
                          <span className="search-project-detail-label">Location:</span>
                          <span className="search-project-detail-value">{project.location}</span>
                        </div>
                        <div className="search-project-detail-item">
                          <span className="search-project-detail-label">Type:</span>
                          <span className="search-project-detail-value">{project.type}</span>
                        </div>
                        <div className="search-project-detail-item">
                          <span className="search-project-detail-label">Client:</span>
                          <span className="search-project-detail-value">{project.client}</span>
                        </div>
                        <div className="search-project-detail-item">
                          <span className="search-project-detail-label">Director:</span>
                          <span className="search-project-detail-value">{project.director}</span>
                        </div>
                        <div className="search-project-detail-item">
                          <span className="search-project-detail-label">Status:</span>
                          <span className="search-project-detail-value">{project.status}</span>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

export default SearchResults;
