import React from 'react';
import './App.css';

function App() {
  const portfolioItems = [
    {
      id: 1,
      title: "현대 건축물 1",
      description: "화이트 모던 건축",
      imageUrl: "/images/portfolio/architecture/modern-building-01.jpg",
      size: "large"
    },
    {
      id: 2,
      title: "주거 복합시설",
      description: "친환경 설계",
      imageUrl: "/images/portfolio/residential/family-house-01.jpg",
      size: "medium"
    },
    {
      id: 3,
      title: "모던 인테리어",
      description: "미니멀 공간",
      imageUrl: "/images/portfolio/interior/minimal-living-room.jpg",
      size: "small"
    },
    {
      id: 4,
      title: "메탈 파사드",
      description: "산업적 디자인",
      imageUrl: "",
      size: "medium"
    },
    {
      id: 5,
      title: "클래식 인테리어",
      description: "대리석 기둥",
      imageUrl: "/images/portfolio/interior/marble-bathroom.jpg",
      size: "large"
    },
    {
      id: 6,
      title: "전원 주택",
      description: "자연과 조화",
      imageUrl: "",
      size: "small"
    },
    {
      id: 7,
      title: "현대 주택",
      description: "기하학적 형태",
      imageUrl: "/images/portfolio/architecture/white-house-exterior.jpg",
      size: "medium"
    }
  ];

  return (
    <div className="App">
      <header className="main-header">
        <div className="header-content">
          <h1 className="main-title">희생동</h1>
          <div className="search-menu">
            <button className="search-btn">🔍</button>
            <button className="menu-btn">☰</button>
          </div>
        </div>
      </header>

      <main className="portfolio-grid">
        {portfolioItems.map(item => (
          <div key={item.id} className={`portfolio-item ${item.size}`}>
            <div className="image-container">
              {item.imageUrl ? (
                <img 
                  src={item.imageUrl} 
                  alt={item.title}
                  className="portfolio-image"
                  onError={(e) => {
                    e.target.style.display = 'none';
                    e.target.nextSibling.style.display = 'flex';
                  }}
                />
              ) : null}
              <div className={`image-placeholder ${item.imageUrl ? 'hidden' : ''}`}>
                <div className="placeholder-content">
                  <span className="placeholder-text">이미지 {item.id}</span>
                  <small className="placeholder-guide">
                    {item.imageUrl ? '이미지 로드 실패' : '이미지를 추가해주세요'}
                  </small>
                </div>
              </div>
            </div>
            <div className="item-info">
              <h3 className="item-title">{item.title}</h3>
              <p className="item-description">{item.description}</p>
            </div>
          </div>
        ))}
      </main>
    </div>
  );
}

export default App;
