import React, { useState } from 'react';
import './Book.css';

const Book = () => {
  const [bookSize, setBookSize] = useState('medium'); // 기본 크기는 medium
  // 책 데이터 (이미지의 책들을 참고)
  const books = [
    {
      id: 1,
      title: "건축 재료의 새로운 시고",
      image: "/images/books/material-matter.jpg", // 실제 이미지 경로로 변경 필요
      link: "https://example.com/book1",
      description: "건축 재료에 대한 새로운 관점",
      size: "large"
    },
    {
      id: 2,
      title: "체인지업그라운드 포항 CHANGUP GROUND",
      image: "/images/books/changup-ground.jpg", // 실제 이미지 경로로 변경 필요
      link: "https://example.com/book2",
      description: "포항의 새로운 변화 공간",
      size: "medium"
    },
    {
      id: 3,
      title: "Compound Body",
      image: "/images/books/compound-body.jpg", // 실제 이미지 경로로 변경 필요
      link: "https://example.com/book3",
      description: "복합적 건축 공간에 대한 연구",
      size: "small"
    },
    {
      id: 4,
      title: "Compound Body",
      image: "/images/books/compound-body-2.jpg", // 실제 이미지 경로로 변경 필요
      link: "https://example.com/book4",
      description: "복합적 건축 공간에 대한 연구 2",
      size: "medium"
    },
    {
      id: 5,
      title: "건축 재료의 새로운 시고",
      image: "/images/books/material-matter-2.jpg", // 실제 이미지 경로로 변경 필요
      link: "https://example.com/book5",
      description: "건축 재료에 대한 새로운 관점 2",
      size: "large"
    },
    {
      id: 6,
      title: "체인지업그라운드 포항 CHANGUP GROUND",
      image: "/images/books/changup-ground-2.jpg", // 실제 이미지 경로로 변경 필요
      link: "https://example.com/book6",
      description: "포항의 새로운 변화 공간 2",
      size: "small"
    }
  ];

  const handleBookClick = (link) => {
    window.open(link, '_blank', 'noopener,noreferrer');
  };

  return (
    <div className="book-container">
      <h1 className="book-page-title">BOOK</h1>

      {/* 크기 선택 버튼 */}
      <div className="book-size-controls">
        <button 
          className={`size-button ${bookSize === 'small' ? 'active' : ''}`}
          onClick={() => setBookSize('small')}
        >
          Small
        </button>
        <button 
          className={`size-button ${bookSize === 'medium' ? 'active' : ''}`}
          onClick={() => setBookSize('medium')}
        >
          Medium
        </button>
        <button 
          className={`size-button ${bookSize === 'large' ? 'active' : ''}`}
          onClick={() => setBookSize('large')}
        >
          Large
        </button>
      </div>

      <div className={`book-grid book-grid-${bookSize}`}>
        {books.map((book) => (
          <div 
            key={book.id} 
            className="book-item"
            onClick={() => handleBookClick(book.link)}
          >
            <div className="book-image-wrapper">
              <img 
                src={book.image} 
                alt={book.title}
                className="book-image"
                onError={(e) => {
                  e.target.src = 'data:image/svg+xml;base64,';
                }}
              />
            </div>
            <div className="book-info">
              <h3 className="book-title">{book.title}</h3>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Book;
