import React, { useRef, useEffect } from 'react';
import p5 from 'p5';

const OrganizationDiagram = () => {
  const canvasRef = useRef();

  useEffect(() => {
    const sketch = (p) => {
      let centerX, centerY;
      let radius = 200;
      let orgs = [];
      let time = 0;
      let logoImg;

      // 조직 데이터
      const orgData = [
        { name: '운생동 건축사사무소', nameEn: 'UNSANGDONG ARCHITECTS', speed: 1, offset: 0 },
        { name: '스페이스 코디네이터', nameEn: 'SPACE COORDINATOR', speed: 0.8, offset: p.PI/2 },
        { name: 'UP 출판사', nameEn: 'UP PUBLISHING CO.', speed: 1.2, offset: p.PI },
        { name: 'USD 디자인', nameEn: 'USD DESIGN', speed: 0.9, offset: 3*p.PI/2 }
      ];

      p.preload = () => {
        // 로고 이미지 로드 (필요시)
        // logoImg = p.loadImage('logo-url');
      };

      p.setup = () => {
        const canvas = p.createCanvas(800, 600);
        canvas.parent(canvasRef.current);
        centerX = p.width / 2;
        centerY = p.height / 2;

        // 조직 객체 초기화
        orgData.forEach((data, index) => {
          orgs.push({
            ...data,
            x: 0,
            y: 0,
            targetX: 0,
            targetY: 0,
            hovered: false,
            wobble: p.random(0, p.TWO_PI) // 각각 다른 wobble 시작점
          });
        });
      };

      p.draw = () => {
        p.background(250, 250, 250);
        time += 0.005; // 전체 회전 속도

        // 조직들 위치 업데이트 및 그리기
        orgs.forEach((org, index) => {
          // 기본 원형 궤도 + 자연스러운 wobble 효과
          const angle = time * org.speed + org.offset;
          const wobbleX = p.sin(time * 3 + org.wobble) * 15; // x축 흔들림
          const wobbleY = p.cos(time * 2.5 + org.wobble) * 10; // y축 흔들림

          org.targetX = centerX + p.cos(angle) * radius + wobbleX;
          org.targetY = centerY + p.sin(angle) * radius + wobbleY;

          // 부드러운 이동 (easing)
          org.x = p.lerp(org.x, org.targetX, 0.1);
          org.y = p.lerp(org.y, org.targetY, 0.1);

          // 마우스 호버 체크
          const distance = p.dist(p.mouseX, p.mouseY, org.x, org.y);
          org.hovered = distance < 80;

          // 연결선 그리기
          drawConnection(org.x, org.y);

          // 조직 박스 그리기
          drawOrganization(org, index);
        });

        // 중앙 허브 그리기
        drawCenterHub();
      };

      const drawConnection = (x, y) => {
        p.stroke(0, 0, 0, 100);
        p.strokeWeight(1.5);

        // 곡선으로 연결 (베지어 곡선)
        const controlX = (centerX + x) / 2 + p.sin(time * 2) * 20;
        const controlY = (centerY + y) / 2 + p.cos(time * 1.5) * 15;

        p.noFill();
        p.bezier(centerX, centerY, controlX, controlY, controlX, controlY, x, y);
      };

      const drawOrganization = (org, index) => {
        p.push();
        p.translate(org.x, org.y);

        // 호버시 확대 효과
        const scale = org.hovered ? 1.1 : 1;
        p.scale(scale);

        // 배경 박스
        p.fill(255, 255, 255, 240);
        p.stroke(0);
        p.strokeWeight(org.hovered ? 2 : 1);
        p.rect(-80, -25, 160, 50, 8);

        // 텍스트
        p.fill(0);
        p.noStroke();
        p.textAlign(p.CENTER, p.CENTER);
        p.textSize(12);
        p.textFont('Arial');
        p.text(org.name, 0, -8);

        p.textSize(10);
        p.fill(100);
        p.text(org.nameEn, 0, 8);

        p.pop();
      };

      const drawCenterHub = () => {
        p.push();
        p.translate(centerX, centerY);

        // 중앙 원 배경
        p.fill(255);
        p.stroke(0);
        p.strokeWeight(2);
        p.ellipse(0, 0, 180, 180);

        // 로고 텍스트 (이미지 대신)
        p.fill(0);
        p.noStroke();
        p.textAlign(p.CENTER, p.CENTER);
        p.textSize(16);
        p.textFont('Arial Bold');
        p.text('UNSAENGDONG', 0, -15);

        p.textSize(12);
        p.fill(100);
        p.text('ARCHITECTS', 0, 5);
        p.text('COOPERATION', 0, 20);

        p.pop();
      };

      p.windowResized = () => {
        const container = canvasRef.current.parentElement;
        const containerWidth = container.offsetWidth;
        const newHeight = Math.min(600, containerWidth * 0.75);
        p.resizeCanvas(containerWidth, newHeight);
        centerX = p.width / 2;
        centerY = p.height / 2;
        radius = Math.min(p.width, p.height) * 0.25;
      };
    };

    const p5Instance = new p5(sketch);

    return () => {
      p5Instance.remove();
    };
  }, []);

  return (
    <div className="organization-p5-container">
      <div ref={canvasRef}></div>
    </div>
  );
};

export default OrganizationDiagram;
