import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router } from 'react-router-dom';
import './App.css';
import Header from './components/Header';
import Main from './components/Main';
import About from './components/About';
import Awards from './components/Awards';
import Contact from './components/Contact';
import Architecture from './components/Architecture';
import ProjectDetail from './components/ProjectDetail';
import Art from './components/Art';
import Design from './components/Design';

function App() {
  const [currentPage, setCurrentPage] = useState('main');
  const [selectedProjectId, setSelectedProjectId] = useState(null);

  // 브라우저 뒤로가기/앞으로가기 처리
  useEffect(() => {
    const handlePopState = (event) => {
      console.log('popstate event:', event.state);
      if (event.state) {
        setCurrentPage(event.state.page);
        setSelectedProjectId(event.state.projectId || null);
      } else {
        // 초기 상태로 돌아감
        setCurrentPage('main');
        setSelectedProjectId(null);
      }
    };

    window.addEventListener('popstate', handlePopState);

    return () => {
      window.removeEventListener('popstate', handlePopState);
    };
  }, []);

  const handleNavigate = (page, projectId = null) => {
    console.log('handleNavigate called with:', page, projectId);
    setCurrentPage(page);
    setSelectedProjectId(projectId);

    // URL 업데이트
    if (page === 'project-detail' && projectId) {
      window.history.pushState({ page, projectId }, '', `/project/${projectId}`);
    } else if (page !== 'project-detail') {
      const url = page === 'main' ? '/' : `/${page}`;
      window.history.pushState({ page }, '', url);
    }
  };

  const renderPage = () => {
    switch(currentPage) {
      case 'about':
        return <About />;
      case 'awards':
        return <Awards />;
      case 'contact':
        return <Contact />;
      case 'architecture':
        return <Architecture onNavigate={handleNavigate} />;
      case 'project-detail':
        return <ProjectDetail projectId={selectedProjectId} onNavigate={handleNavigate} />;
      case 'art':
        return <Art onNavigate={handleNavigate} />;
      case 'design':
        return <Design onNavigate={handleNavigate} />;
      case 'main':
      default:
        return <Main />;
    }
  };

  return (
    <Router>
      <div className="App">
        <Header 
          currentPage={currentPage}
          onNavigate={handleNavigate}
        />
        {renderPage()}
      </div>
    </Router>
  );
}

export default App;
