import React from 'react';
import './Main.css';

function Main() {
  // 랜덤 선택 함수
  const getRandomImages = (imageList, count) => {
    const shuffled = [...imageList].sort(() => 0.5 - Math.random());
    return shuffled.slice(0, count);
  };

  // 세로 이미지 리스트 (실제 파일명으로 수정 필요)
  const verticalImages = [
    'vertical-1.jpg',
    'vertical-2.jpg', 
    'vertical-3.jpg',
    'vertical-4.jpg',
    'vertical-5.jpg',
    'vertical-6.jpg',
    'vertical-7.jpg',
    'vertical-8.jpg'
    // 필요에 따라 더 추가
  ];

  // 가로 이미지 리스트 (실제 파일명으로 수정 필요)
  const horizonImages = [
    'horizon-1.jpg',
    'horizon-2.jpg',
    'horizon-3.jpg',
    'horizon-4.jpg'
    // 필요에 따라 더 추가
  ];

  // 랜덤하게 선택된 이미지들
  const selectedVerticalImages = getRandomImages(verticalImages, 5);
  const selectedHorizonImages = getRandomImages(horizonImages, 2);

  return (
    <main className="main-container">
      <div className="main-image-wrap">
        {selectedVerticalImages.map((image, index) => (
          <div key={`vertical-${index}`} className={`main-image-ver main-image${index + 1}`}>
            <img src={`/images/main/vertical/${image}`} alt={`세로 이미지 ${index + 1}`} />
          </div>
        ))}
        {selectedHorizonImages.map((image, index) => (
          <div key={`horizon-${index}`} className={`main-image-hor main-image${index + 6}`}>
            <img src={`/images/main/horizon/${image}`} alt={`가로 이미지 ${index + 1}`} />
          </div>
        ))}
      </div>
    </main>
  );
}

export default Main;
