import React, { useState } from 'react';
import './News.css';

const News = () => {
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 10;

  // 샘플 뉴스 데이터
  const newsData = {
    important: [
      {
        id: 1,
        title: "[중요] 2024년 건축 프로젝트 공모전 개최 안내",
        created_at: "2024-01-15",
        views: 245,
        is_important: true
      },
      {
        id: 2,
        title: "[공지] 설계사무소 이전 안내",
        created_at: "2024-01-10",
        views: 189,
        is_important: true
      }
    ],
    regular: [
      {
        id: 3,
        title: "2024 한국건축문화대상 수상",
        created_at: "2024-01-20",
        views: 156
      },
      {
        id: 4,
        title: "지속가능한 건축 디자인 세미나 참석",
        created_at: "2024-01-18",
        views: 98
      },
      {
        id: 5,
        title: "신규 프로젝트 착공식 개최",
        created_at: "2024-01-16",
        views: 134
      },
      {
        id: 6,
        title: "건축 전시회 참가 소식",
        created_at: "2024-01-14",
        views: 87
      },
      {
        id: 7,
        title: "친환경 건축 기술 도입",
        created_at: "2024-01-12",
        views: 112
      },
      {
        id: 8,
        title: "국제 건축 심포지엄 발표",
        created_at: "2024-01-08",
        views: 76
      },
      {
        id: 9,
        title: "새로운 건축 기법 연구 성과",
        created_at: "2024-01-05",
        views: 145
      },
      {
        id: 10,
        title: "건축가 인터뷰 기사 게재",
        created_at: "2024-01-03",
        views: 203
      }
    ]
  };

  const totalCount = newsData.regular.length;
  const totalPages = Math.ceil(totalCount / itemsPerPage);
  const pageGroupSize = 5;
  const currentGroup = Math.floor((currentPage - 1) / pageGroupSize);
  const startPage = currentGroup * pageGroupSize + 1;
  const endPage = Math.min(startPage + pageGroupSize - 1, totalPages);

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('ko-KR', {
      year: 'numeric',
      month: '2-digit',
      day: '2-digit'
    });
  };

  const handleNewsClick = (news) => {
    console.log('뉴스 클릭:', news.title);
    // 여기에 뉴스 상세 페이지로 이동하는 로직 추가 가능
  };

  const handlePageChange = (page) => {
    setCurrentPage(page);
    window.scrollTo(0, 0);
  };

  const renderPagination = () => {
    if (totalPages <= 1) return null;

    return (
      <div className="pagination">
        {totalPages > pageGroupSize && currentGroup > 0 && (
          <button 
            onClick={() => handlePageChange(startPage - 1)}
          >
            이전
          </button>
        )}
        {Array.from(
          { length: endPage - startPage + 1 }, 
          (_, i) => startPage + i
        ).map(page => (
          <button
            key={page}
            onClick={() => handlePageChange(page)}
            className={currentPage === page ? 'active' : ''}
          >
            {page}
          </button>
        ))}
        {totalPages > pageGroupSize && endPage < totalPages && (
          <button 
            onClick={() => handlePageChange(endPage + 1)}
          >
            다음
          </button>
        )}
      </div>
    );
  };

  const renderNewsRow = (news, isImportant = false) => (
    <tr 
      key={news.id} 
      onClick={() => handleNewsClick(news)} 
      style={{ cursor: 'pointer' }}
      className={isImportant ? 'important-row' : ''}
    >
      <td className="news-title">
        {news.title}
      </td>
      <td className="news-date">{formatDate(news.created_at)}</td>
      <td className="news-views">{news.views || 0}</td>
      <td className="news-meta-cell" colSpan="2">
        <div className="news-meta-row">
          <span className="news-date">{formatDate(news.created_at)}</span>
          <span className="news-sep">ㅣ</span>
          <span className="news-views">조회수 {news.views || 0}</span>
        </div>
      </td>
    </tr>
  );

  // 현재 페이지에 해당하는 일반 뉴스 가져오기
  const startIndex = (currentPage - 1) * itemsPerPage;
  const endIndex = startIndex + itemsPerPage;
  const currentRegularNews = newsData.regular.slice(startIndex, endIndex);

  return (
    <div className="news-container">
      <h1 className="page-title">News</h1>
      <div className="news-board">
        <table className="news-table">
          <thead>
            <tr>
              <th>제목</th>
              <th>작성일</th>
              <th>조회수</th>
            </tr>
          </thead>
          <tbody>
            {newsData.important.map(news => renderNewsRow(news, true))}
            {currentRegularNews.length > 0 ? (
              currentRegularNews.map(news => renderNewsRow(news))
            ) : (
              <tr>
                <td colSpan="3" className="no-data">등록된 뉴스가 없습니다.</td>
              </tr>
            )}
          </tbody>
        </table>
        {renderPagination()}
      </div>
    </div>
  );
};

export default News;
