import React, { useState, useEffect } from 'react';
import './Architecture.css';

function Architecture({ onNavigate }) {
  const [selectedYear, setSelectedYear] = useState('');
  const [selectedType, setSelectedType] = useState('');
  const [viewMode, setViewMode] = useState('gallery');
  const [hoveredProject, setHoveredProject] = useState(null);
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });

  const projects = [
    {
      id: 1,
      title: "Project A",
      year: "2024",
      type: "Public Library",
      image: "./images/Architecture/arch-1.jpg",
      images: [
        "./images/Architecture/arch-1.jpg",
        "./images/Architecture/detail/detail 1.jpg",
        "./images/Architecture/detail/detail 2.jpg"
      ],
      description: "Lorem ipsum dolor sit amet, consectetur adipiscing elit."
    },
    {
      id: 2,
      title: "Project B",
      year: "2023",
      type: "Sports Complex",
      image: "./images/Architecture/arch-2.jpg",
      images: [
        "./images/Architecture/arch-2.jpg",
        "./images/Architecture/detail/detail 3.jpg",
        "./images/Architecture/detail/detail 4.jpg"
      ],
      description: "Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua."
    },
    {
      id: 3,
      title: "Project C",
      year: "2022",
      type: "Residential Community",
      image: "./images/Architecture/arch-3.jpg",
      images: [
        "./images/Architecture/arch-3.jpg",
        "./images/Architecture/detail/main.jpg",
        "./images/Architecture/detail/detail 1.jpg"
      ],
      description: "Ut enim ad minim veniam, quis nostrud exercitation."
    },
    {
      id: 4,
      title: "Project D",
      year: "2021",
      type: "Cultural Center",
      image: "./images/Architecture/arch-4.jpg",
      images: [
        "./images/Architecture/arch-4.jpg",
        "./images/Architecture/detail/detail 2.jpg",
        "./images/Architecture/detail/detail 3.jpg"
      ],
      description: "Duis aute irure dolor in reprehenderit in voluptate."
    },
    {
      id: 5,
      title: "Project E",
      year: "2020",
      type: "Office Building",
      image: "./images/Architecture/arch-5.jpg",
      images: [
        "./images/Architecture/arch-5.jpg",
        "./images/Architecture/detail/detail 4.jpg",
        "./images/Architecture/detail/main.jpg"
      ],
      description: "Excepteur sint occaecat cupidatat non proident."
    },
    {
      id: 6,
      title: "Project F",
      year: "2019",
      type: "Government Building",
      image: "./images/Architecture/arch-6.jpg",
      images: [
        "./images/Architecture/arch-6.jpg",
        "./images/Architecture/detail/detail 1.jpg",
        "./images/Architecture/detail/detail 2.jpg"
      ],
      description: "Sunt in culpa qui officia deserunt mollit anim."
    }
  ];

  // 필터링된 프로젝트 목록
  const filteredProjects = projects.filter(project => {
    const yearMatch = selectedYear === '' || project.year === selectedYear;
    const typeMatch = selectedType === '' || project.type === selectedType;
    return yearMatch && typeMatch;
  });

  // 유니크한 년도와 타입 추출
  const years = [...new Set(projects.map(project => project.year))].sort((a, b) => b - a);
  const types = [...new Set(projects.map(project => project.type))].sort();

  // 마우스 위치 추적
  useEffect(() => {
    const handleMouseMove = (e) => {
      setMousePosition({ x: e.clientX, y: e.clientY });
    };

    if (hoveredProject) {
      document.addEventListener('mousemove', handleMouseMove);
    }

    return () => {
      document.removeEventListener('mousemove', handleMouseMove);
    };
  }, [hoveredProject]);

  const handleProjectClick = (projectId) => {
    console.log('Project clicked:', projectId);
    console.log('onNavigate function:', onNavigate);
    onNavigate('project-detail', projectId);
  };

  const handleYearChange = (e) => {
    setSelectedYear(e.target.value);
  };

  const handleTypeChange = (e) => {
    setSelectedType(e.target.value);
  };

  const handleViewModeChange = (mode) => {
    setViewMode(mode);
  };

  const handleProjectHover = (project) => {
    setHoveredProject(project);
  };

  const handleProjectLeave = () => {
    setHoveredProject(null);
  };

  return (
    <div className="architecture-container">
      {/* 필터와 뷰 옵션 */}
      <div className="controls-bar">
        <div className="filters">
          <div className="filter-group">
            <select 
              value={selectedYear} 
              onChange={handleYearChange}
              className="filter-select"
            >
              <option value="">Year</option>
              {years.map(year => (
                <option key={year} value={year}>{year}</option>
              ))}
            </select>
          </div>

          <div className="filter-group">
            <select 
              value={selectedType} 
              onChange={handleTypeChange}
              className="filter-select"
            >
              <option value="">Type</option>
              {types.map(type => (
                <option key={type} value={type}>{type}</option>
              ))}
            </select>
          </div>
        </div>

        <div className="view-options">
          <button 
            className={`view-btn ${viewMode === 'gallery' ? 'active' : ''}`}
            onClick={() => handleViewModeChange('gallery')}
          >
            GALLERY
          </button>
          <span className="view-separator">/</span>
          <button 
            className={`view-btn ${viewMode === 'list' ? 'active' : ''}`}
            onClick={() => handleViewModeChange('list')}
          >
            LIST
          </button>
        </div>
      </div>

      {/* Gallery 뷰 */}
      {viewMode === 'gallery' && (
        <div className="projects-grid">
          {filteredProjects.map((project) => (
            <div 
              key={project.id} 
              className="project-item"
              onClick={() => handleProjectClick(project.id)}
            >
              <div className="project-image-wrapper">
                <img 
                  src={project.image} 
                  alt={project.title}
                  className="project-image"
                />
                <div className="project-overlay">
                  <div className="project-info">
                    <h3 className="project-title">{project.title}</h3>
                    <div className="project-meta">
                      <span className="project-year">{project.year}</span>
                      <span className="project-type">{project.type}</span>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}

      {/* List 뷰 */}
      {viewMode === 'list' && (
        <div className="projects-list">
          <div className="list-header">
            <div className="list-col-title">Project</div>
            <div className="list-col-year">Year</div>
            <div className="list-col-type">Type</div>
            <div className="list-col-location">Location</div>
            <div className="list-col-area">Area</div>
          </div>
          {filteredProjects.map((project) => (
            <div 
              key={project.id} 
              className="list-item"
              onClick={() => handleProjectClick(project.id)}
              data-year={project.year}
              data-type={project.type}
              data-location={project.location}
              data-area={project.area}
            >
              <div className="list-col-title">
                <div 
                  className="list-project-title"
                  onMouseEnter={() => handleProjectHover(project)}
                  onMouseLeave={handleProjectLeave}
                >
                  {project.title}
                </div>
                <div className="list-project-title-en">{project.titleEn}</div>
              </div>
              <div className="list-col-year">{project.year}</div>
              <div className="list-col-type">{project.type}</div>
              <div className="list-col-location">{project.location}</div>
              <div className="list-col-area">{project.area}</div>
            </div>
          ))}
        </div>
      )}

      {/* 호버 썸네일 */}
      {hoveredProject && viewMode === 'list' && (
        <div 
          className="hover-thumbnail"
          style={{
            left: mousePosition.x + 20,
            top: mousePosition.y - 100,
          }}
        >
          <img 
            src={hoveredProject.image} 
            alt={hoveredProject.title}
            className="hover-thumbnail-image"
          />
        </div>
      )}
    </div>
  );
}

export default Architecture;
