import React, { useRef, useEffect, useState } from 'react';
import p5 from 'p5';

const OrganizationDiagram = () => {
  const canvasRef = useRef();
  const p5InstanceRef = useRef();
  const [isLoaded, setIsLoaded] = useState(false);

  useEffect(() => {
    if (!canvasRef.current || p5InstanceRef.current) {
      return;
    }

    const sketch = (p) => {
      let centerX, centerY;
      let radius = 200;
      let orgs = [];
      let time = 0;
      let isSetupComplete = false;

      // 조직 데이터
      const orgData = [
        { name: '운생동 건축사사무소', nameEn: 'UNSANGDONG ARCHITECTS', speed: 1, offset: 0 },
        { name: '스페이스 코디네이터', nameEn: 'SPACE COORDINATOR', speed: 0.8, offset: Math.PI/2 },
        { name: 'UP 출판사', nameEn: 'UP PUBLISHING CO.', speed: 1.2, offset: Math.PI },
        { name: 'USD 디자인', nameEn: 'USD DESIGN', speed: 0.9, offset: 3*Math.PI/2 }
      ];

      p.setup = () => {
        try {
          const canvas = p.createCanvas(800, 600);
          if (canvasRef.current) {
            canvas.parent(canvasRef.current);
          }
          centerX = p.width / 2;
          centerY = p.height / 2;

          // 조직 객체 초기화
          orgs = orgData.map((data, index) => ({
            ...data,
            x: centerX,
            y: centerY,
            targetX: centerX,
            targetY: centerY,
            hovered: false,
            wobble: Math.random() * Math.PI * 2
          }));

          isSetupComplete = true;
          setIsLoaded(true);
        } catch (error) {
          console.error('P5 setup error:', error);
        }
      };

      p.draw = () => {
        if (!isSetupComplete) return;

        try {
          p.background(250, 250, 250);
          time += 0.005;

          // 조직들 위치 업데이트 및 그리기
          orgs.forEach((org, index) => {
            // 기본 원형 궤도 + 자연스러운 wobble 효과
            const angle = time * org.speed + org.offset;
            const wobbleX = Math.sin(time * 3 + org.wobble) * 15;
            const wobbleY = Math.cos(time * 2.5 + org.wobble) * 10;

            org.targetX = centerX + Math.cos(angle) * radius + wobbleX;
            org.targetY = centerY + Math.sin(angle) * radius + wobbleY;

            // 부드러운 이동 (easing)
            org.x = p.lerp(org.x, org.targetX, 0.1);
            org.y = p.lerp(org.y, org.targetY, 0.1);

            // 마우스 호버 체크
            const distance = p.dist(p.mouseX, p.mouseY, org.x, org.y);
            org.hovered = distance < 80;

            // 연결선 그리기
            drawConnection(org.x, org.y);

            // 조직 박스 그리기
            drawOrganization(org, index);
          });

          // 중앙 허브 그리기
          drawCenterHub();
        } catch (error) {
          console.error('P5 draw error:', error);
        }
      };

      const drawConnection = (x, y) => {
        p.stroke(0, 0, 0, 100);
        p.strokeWeight(1.5);

        // 곡선으로 연결 (베지어 곡선)
        const controlX = (centerX + x) / 2 + Math.sin(time * 2) * 20;
        const controlY = (centerY + y) / 2 + Math.cos(time * 1.5) * 15;

        p.noFill();
        p.bezier(centerX, centerY, controlX, controlY, controlX, controlY, x, y);
      };

      const drawOrganization = (org, index) => {
        p.push();
        p.translate(org.x, org.y);

        // 호버시 확대 효과
        const scale = org.hovered ? 1.1 : 1;
        p.scale(scale);

        // 배경 박스
        p.fill(255, 255, 255, 240);
        p.stroke(0);
        p.strokeWeight(org.hovered ? 2 : 1);
        p.rect(-80, -25, 160, 50, 8);

        // 텍스트
        p.fill(0);
        p.noStroke();
        p.textAlign(p.CENTER, p.CENTER);
        p.textSize(12);
        p.text(org.name, 0, -8);

        p.textSize(10);
        p.fill(100);
        p.text(org.nameEn, 0, 8);

        p.pop();
      };

      const drawCenterHub = () => {
        p.push();
        p.translate(centerX, centerY);

        // 중앙 원 배경
        p.fill(255);
        p.stroke(0);
        p.strokeWeight(2);
        p.ellipse(0, 0, 180, 180);

        // 로고 텍스트
        p.fill(0);
        p.noStroke();
        p.textAlign(p.CENTER, p.CENTER);
        p.textSize(16);
        p.text('UNSAENGDONG', 0, -15);

        p.textSize(12);
        p.fill(100);
        p.text('ARCHITECTS', 0, 5);
        p.text('COOPERATION', 0, 20);

        p.pop();
      };

      p.windowResized = () => {
        if (!isSetupComplete || !canvasRef.current) return;

        try {
          const container = canvasRef.current.parentElement;
          if (container) {
            const containerWidth = container.offsetWidth;
            const newHeight = Math.min(600, containerWidth * 0.75);
            p.resizeCanvas(containerWidth, newHeight);
            centerX = p.width / 2;
            centerY = p.height / 2;
            radius = Math.min(p.width, p.height) * 0.25;
          }
        } catch (error) {
          console.error('P5 resize error:', error);
        }
      };
    };

    try {
      p5InstanceRef.current = new p5(sketch);
    } catch (error) {
      console.error('Failed to create P5 instance:', error);
    }

    return () => {
      if (p5InstanceRef.current) {
        try {
          p5InstanceRef.current.remove();
          p5InstanceRef.current = null;
        } catch (error) {
          console.error('Error removing P5 instance:', error);
        }
      }
    };
  }, []);

  if (!isLoaded) {
    return (
      <div className="organization-loading">
        <div style={{
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          height: '400px',
          fontSize: '18px',
          color: '#666'
        }}>
          조직도를 로딩 중...
        </div>
      </div>
    );
  }

  return (
    <div className="organization-p5-container">
      <div ref={canvasRef}></div>
    </div>
  );
};

export default OrganizationDiagram;
