import React, { useState } from 'react';
import './Book.css';

const Book = () => {
  // 책 데이터 (이미지의 책들을 참고)
  const books = [
    {
      id: 1,
      title: "건축 재료의 새로운 사고",
      image: "/images/book/book1.jpg", // 실제 이미지 경로로 변경 필요
      link: "https://example.com/book1",
      description: "건축 재료에 대한 새로운 관점",
      size: "large"
    },
    {
      id: 2,
      title: "체인지업그라운드 포항 CHANGeUP GROUND",
      image: "/images/book/book2.jpg", // 실제 이미지 경로로 변경 필요
      link: "https://example.com/book2",
      description: "포항의 새로운 변화 공간",
      size: "small"
    },
    {
      id: 3,
      title: "Compound Body",
      image: "/images/book/book3.jpg", // 실제 이미지 경로로 변경 필요
      link: "https://example.com/book3",
      description: "복합적 건축 공간에 대한 연구",
      size: "medium"
    },
    {
      id: 4,
      title: "Compound Body",
      image: "/images/book/book3.jpg", // 실제 이미지 경로로 변경 필요
      link: "https://example.com/book4",
      description: "복합적 건축 공간에 대한 연구 2",
      size: "medium"
    },
    {
      id: 5,
      title: "건축 재료의 새로운 사고",
      image: "/images/book/book1.jpg", // 실제 이미지 경로로 변경 필요
      link: "https://example.com/book5",
      description: "건축 재료에 대한 새로운 관점 2",
      size: "large"
    },
    {
      id: 6,
      title: "체인지업그라운드 포항 CHANGeUP GROUND",
      image: "/images/book/book2.jpg", // 실제 이미지 경로로 변경 필요
      link: "https://example.com/book6",
      description: "포항의 새로운 변화 공간 2",
      size: "small"
    }
  ];

  const handleBookClick = (link) => {
    window.open(link, '_blank', 'noopener,noreferrer');
  };

  // 책을 3개씩 묶어서 row로 구성
  const createRows = (books, itemsPerRow = 3) => {
    const rows = [];
    for (let i = 0; i < books.length; i += itemsPerRow) {
      rows.push(books.slice(i, i + itemsPerRow));
    }
    return rows;
  };

  const bookRows = createRows(books);

  return (
    <div className="book-container">
      <div className="book-grid">
        {bookRows.map((row, rowIndex) => (
          <div key={rowIndex} className="book-row">
            {row.map((book) => (
              <div 
                key={book.id} 
                className={`book-item book-item-${book.size}`}
              >
                    <div className="book-image-wrapper">
                        <a href={book.link} target="_blank" rel="noopener noreferrer">
                  <img 
                    src={book.image} 
                    alt={book.title}
                            className="book-image"
                    onError={(e) => {
                      e.target.src = 'data:image/svg+xml;base64,';
                    }}
                            />
                            </a>
                </div>
                <div className="book-info">
                  <h3 className="book-title">{book.title}</h3>
                </div>
              </div>
            ))}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Book;
